COMPONENT.script:
/* ** Procedure Name: ** ** AdjustColumnWidth ** ** Description: ** ** Reduces the input value to fit within the column width limitations ** of the current server. (This is necessary when running on gateways ** to "width challenged" servers.) ** ** Parameters: ** ** normal integer The normal column width we would use on Ingres. ** ** Return Type: ** ** integer The actual width we need to use on the current ** server, which may be less than the normal ** width we wanted to use. ** ** Local Procedures: ** ** (None) ** ** Macro Variables: ** ** (None) ** ** History: ** 14-mar-97 (sandyd) ** Just factoring out a little bit of common code needed to ** adjust column widths when creating strings tables and ** bitmaps tables on gateways to "width challenged" servers. */ procedure AdjustColumnWidth ( normal = integer not null; ) = declare max_width = integer not null; adjusted_width = integer not null; enddeclare { /* ** Assume normal width. Then truncate if iidbcapabilities ** tells us we need to. */ adjusted_width = normal; max_width = CurProcedure.DBSession.max_catalog_column_width;
if ((max_width > 0) AND (max_width < adjusted_width)) then adjusted_width = max_width; endif;
return adjusted_width; }

			procedure Build_SIS_Data() =
declare
bo         = bitmapobject;
BmFilePath = varchar(256) not null with default;
i          = integer not null;
status     = integer not null;
tablename  = varchar(24) not null;
ret_stat   = smallint not null;
servertype = integer not null;
col_width  = varchar(24) not null;
enddeclare
begin

    BmFilePath = demo_directory;

    tablename = 'student';
    ret_stat = Callproc CkTableExists(param_tbl = :tablename);
    if ret_stat = TableFound then
       drop table student;
       endif;

    tablename = 'student_images';
    ret_stat = Callproc CkTableExists(param_tbl = :tablename);
    if ret_stat = TableFound then
       drop table student_images;
       endif;

   tablename = 'student_info';
    ret_stat = Callproc CkTableExists(param_tbl = :tablename);
    if ret_stat = TableFound then
       drop table student_info;
       endif;

   tablename = 'fees';
    ret_stat = Callproc CkTableExists(param_tbl = :tablename);
    if ret_stat = TableFound then
       drop table fees;
       endif;

   tablename = 'grades';
    ret_stat = Callproc CkTableExists(param_tbl = :tablename);
    if ret_stat = TableFound then
       drop table grades;
       endif;

   tablename = 'location';
    ret_stat = Callproc CkTableExists(param_tbl = :tablename);
    if ret_stat = TableFound then
       drop table location;
       endif;

   tablename = 'parent_info';
    ret_stat = Callproc CkTableExists(param_tbl = :tablename);
    if ret_stat = TableFound then
       drop table parent_info;
       endif;

   tablename = 'attendance_detail';
    ret_stat = Callproc CkTableExists(param_tbl = :tablename);
    if ret_stat = TableFound then
       drop table attendance_detail;
       endif;

/****************************************************/
/* conditional processing based on type of database */
/****************************************************/

    servertype = CurProcedure.DBSession.ServerType;

    if (servertype = DS_INGRES_DBMS) then

        if (NativeIngres = TRUE) then

            create table student(
                         student_id      integer,
                         student_name    varchar(30), 
                         student_loc_id  integer, 
                         student_picture varchar(76));

           modify student to isam unique on student_name, student_id;

           create table student_info (
                        si_id      integer, 
                        si_address varchar(30), 
                        si_city    varchar(15), 
                        si_state   char(2), 
                        si_zip     varchar(9));

           modify student_info to hash on si_id;

           create table grades (
                        grade_student_id integer, 
                        grade_semester   varchar(10), 
                        grade_course     varchar(30), 
                        grade_grade      varchar(3));

           modify grades to btree on grade_student_id;

           create table location (
                        location_id   integer, 
                        location_name varchar(30));

           modify location to hash on location_id;

           create table parent_info(
                        pi_student_id  integer, 
                        pi_father      varchar(30), 
                        pi_mother      varchar(30), 
                        pi_father_home varchar(12), 
                        pi_mother_home varchar(12), 
                        pi_father_work varchar(12), 
                        pi_mother_work varchar(12));

            modify parent_info to hash on pi_student_id;

            create table student_images(
                         picture_id   integer       not null,
                         row_sequence integer       not null,
                         text_total   integer       not null,
                         text_value   varchar(1786) not null)
                   with noduplicates;

           modify student_images to btree unique on picture_id, row_sequence
           with nonleaffill = 80, leaffill = 70, fillfactor = 80;

           create table fees(
                        fee_student_id     integer, 
                        fee_total_to_date  money, 
                        fee_semester_total money, 
                        fee_amount_paid    money, 
                        fee_amount_granted money);

           modify fees to hash on fee_student_id;

           create table attendance_detail(
                        ad_student_id      integer, 
                        ad_absence_tardy   char(1), 
                        ad_date            date,
                        ad_excused_unexcus varchar(10), 
                        ad_teacher         varchar(30), 
                        ad_period          integer);

            modify attendance_detail to isam on ad_student_id;

        else 

           create table student(
                        student_id      integer,
                        student_name    varchar(30), 
                        student_loc_id  integer, 
                        student_picture varchar(76));

           create unique index student_idx on student (student_name);

           create table student_info (
                        si_id      integer, 
                        si_address varchar(30), 
                        si_city    varchar(15), 
                        si_state   char(2), 
                        si_zip     varchar(9));

           create unique index student_info_idx on student_info (si_id);

           create table grades (
                        grade_student_id integer, 
                        grade_semester   varchar(10), 
                        grade_course     varchar(30), 
                        grade_grade      varchar(3));

           create index grades_idx on grades (grade_student_id);

           create table location (
                        location_id   integer, 
                        location_name varchar(30));

           create unique index location_idx on location (location_id);

           create table parent_info(
                        pi_student_id  integer, 
                        pi_father      varchar(30), 
                        pi_mother      varchar(30), 
                        pi_father_home varchar(12), 
                        pi_mother_home varchar(12), 
                        pi_father_work varchar(12), 
                        pi_mother_work varchar(12));

           create unique index parent_info_idx on parent_info (pi_student_id);

	   /*
	   ** Adjust column width of bitmap table if we are connected
	   ** via gateway to a "width challenged" server.
	   */
	   col_width = varchar(AdjustColumnWidth(normal=1786));
	   EXECUTE IMMEDIATE
			'create table student_images '            	   + 
			'( '                                      	   +
			    'picture_id   integer  not null, ' +
			    'row_sequence integer  not null, ' +
			    'text_total   integer  not null, ' +
			    'text_value   varchar(' + col_width + ') '     +
			    			  'not null  '  +
			')';

            create unique index student_images_idx on student_images
                                (picture_id, row_sequence);

           create table fees(
                        fee_student_id     integer, 
                        fee_total_to_date  float, 
                        fee_semester_total float, 
                        fee_amount_paid    float, 
                        fee_amount_granted float);

           create unique index fees_idx on fees (fee_student_id);

           create table attendance_detail(
                        ad_student_id      integer, 
                        ad_absence_tardy   char(1), 
                        ad_date            date,
                        ad_excused_unexcus varchar(10), 
                        ad_teacher         varchar(30), 
                        ad_period          integer);

            create index attendance_det_idx on attendance_detail                          (ad_student_id);

        endif;

    elseif (servertype = DS_INGRESDSK_DBMS) then

       EXECUTE IMMEDIATE
       'create table fees '                    + 
       '( '                                    +
          'fee_student_id integer, '           + 
          'fee_total_to_date decimal(14,2), '  +
          'fee_semester_total decimal(14,2), ' +
          'fee_amount_paid decimal(14,2), '    +
          'fee_amount_granted decimal(14,2), ' + 
          'PRIMARY KEY (fee_student_id) '      + 
       ')';

       EXECUTE IMMEDIATE
       'create unique index fees_idx on fees (fee_student_id)';


       EXECUTE IMMEDIATE
       'create table attendance_detail '     + 
       '( '                                  +
          'ad_student_id      integer, '     +
          'ad_absence_tardy   char(1), '     +
          'ad_date            datetime, '    +
          'ad_excused_unexcus varchar(10), ' +
          'ad_teacher         varchar(30), ' +
          'ad_period          integer, '     +
          'PRIMARY KEY (ad_student_id) '     + 
       ')';

       EXECUTE IMMEDIATE
       'create unique index attend_idx on attendance_detail (ad_student_id)';


       EXECUTE IMMEDIATE
       'create table student '            + 
       '( '                               +
          'student_id      integer, '     +
          'student_name    varchar(30), ' +
          'student_loc_id  integer, '     +
          'student_picture varchar(76), ' +
          'PRIMARY KEY (student_name,student_id) '   + 
       ')';

       EXECUTE IMMEDIATE
       'create unique index student_idx ' +
       ' on student (student_name, student_id)';

       EXECUTE IMMEDIATE
       'create table student_images '            + 
       '( '                                      +
          'picture_id   integer      not null, ' +
          'row_sequence integer      not null, ' +
          'text_total   integer      not null, ' +
          'text_value   varchar(254) not null, ' +
          'PRIMARY KEY (picture_id, row_sequence) ' + 
       ')';

       EXECUTE IMMEDIATE
       'create unique index student_images_idx ' +
       'on student_images (picture_id, row_sequence)';

       EXECUTE IMMEDIATE
       'create table student_info ' +
       '( '                         +
         'si_id      integer, '     +
         'si_address varchar(30), ' + 
         'si_city    varchar(15), ' +
         'si_state   char(2), '     +
         'si_zip     varchar(9), '  +
         'PRIMARY KEY (si_id) '     +
       ')';

       EXECUTE IMMEDIATE
       'create unique index student_info_idx on student_info (si_id)';

       EXECUTE IMMEDIATE
       'create table grades '             +
       '( '                               +
         'grade_student_id integer, '     +
         'grade_semester   varchar(10), ' +
         'grade_course     varchar(30), ' +
         'grade_grade      varchar(3) '   +
         'PRIMARY KEY (grade_student_id) ' +
       ')';

       EXECUTE IMMEDIATE
       'create index grades_idx on grades (grade_student_id)';

       EXECUTE IMMEDIATE 
       'create table location '        +
       '( '                            +
         'location_id   integer, '     + 
         'location_name varchar(30), ' +
         'PRIMARY KEY (location_id) '  +
       ')';

       EXECUTE IMMEDIATE
       'create unique index location_idx on location (location_id)';

       EXECUTE IMMEDIATE
       'create table parent_info '    +
       '( '                           +
       'pi_student_id  integer, '     +
       'pi_father      varchar(30), ' +
       'pi_mother      varchar(30), ' +
       'pi_father_home varchar(12), ' +
       'pi_mother_home varchar(12), ' +
       'pi_father_work varchar(12), ' +
       'pi_mother_work varchar(12), ' +
       'PRIMARY KEY (pi_student_id) ' +
       ')';

       EXECUTE IMMEDIATE
       'create unique index parent_info_idx on parent_info (pi_student_id)';

    elseif (servertype = DS_ORACLE_DBMS) then

       EXECUTE IMMEDIATE
       'create table fees '                    + 
       '( '                                    +
          'fee_student_id     integer, '       + 
          'fee_total_to_date  number(14,2), '  +
          'fee_semester_total number(14,2), '  +
          'fee_amount_paid    number(14,2), '  +
          'fee_amount_granted number(14,2), '  + 
          'CONSTRAINT fees_pk '                +
          'PRIMARY KEY (fee_student_id) '      + 
       ')';

       EXECUTE IMMEDIATE
       'create table attendance_detail '      + 
       '( '                                   +
          'ad_student_id      integer, '      +
          'ad_absence_tardy   char(1), '      +
          'ad_date            date, '         +
          'ad_excused_unexcus varchar2(10), ' +
          'ad_teacher         varchar2(30), ' +
          'ad_period          integer '       +
       ')'; 

     EXECUTE IMMEDIATE
     'create index attend_idx on attendance_table (ad_student_id)';

     EXECUTE IMMEDIATE
       'create table student '          + 
       '( '                             +
       'student_id      integer, '      +
       'student_name    varchar2(30), ' +
       'student_loc_id  integer, '      +
       'student_picture varchar2(76), ' +
       'CONSTRAINT student_id_pk '      +
       'PRIMARY KEY (student_name) '    + 
       ')';

       EXECUTE IMMEDIATE
       'create table student_images '              + 
       '( '                                        +
          'picture_id   integer        not null, ' +
          'row_sequence integer        not null, ' +
          'text_total   integer        not null, ' +
          'text_value   varchar2(1786) not null, ' +
          'CONSTRAINT student_images_pk '          +
          'PRIMARY KEY (picture_id, row_sequence) ' + 
        ')';

       EXECUTE IMMEDIATE
       'create table student_info '  +
       '( '                          +
         'si_id      integer, '      +
         'si_address varchar2(30), ' + 
         'si_city    varchar2(15), ' +
         'si_state   char(2), '      +
         'si_zip     varchar2(9), '  +
         'CONSTRAINT si_pk '         +
         'PRIMARY KEY (si_id) '      +
       ')';

       EXECUTE IMMEDIATE
       'create table grades '              +
       '( '                                +
         'grade_student_id integer, '      +
         'grade_semester   varchar2(10), ' +
         'grade_course     varchar2(30), ' +
         'grade_grade      varchar2(3) '   +
       ')';

       EXECUTE IMMEDIATE
       'create index grades_idx on grades (grade_student_id)';

       EXECUTE IMMEDIATE 
       'create table location '         +
       '( '                             +
         'location_id   integer, '      + 
         'location_name varchar2(30), ' +
         'CONSTRAINT location_pk '      +
         'PRIMARY KEY (location_id) '   +
       ')';

       EXECUTE IMMEDIATE
       'create table parent_info '     +
       '( '                            +
       'pi_student_id  integer, '      +
       'pi_father      varchar2(30), ' +
       'pi_mother      varchar2(30), ' +
       'pi_father_home varchar2(12), ' +
       'pi_mother_home varchar2(12), ' +
       'pi_father_work varchar2(12), ' +
       'pi_mother_work varchar2(12), ' +
       'CONTRAINT parent_pk '          +
       'PRIMARY KEY (pi_student_id) '  +
       ')';

    elseif (servertype = DS_SQLSERVER_DBMS) then

       create table fees(
                    fee_student_id integer not null with default, 
                    fee_total_to_date money not null with default, 
                    fee_semester_total money not null with default, 
                    fee_amount_paid money not null with default, 
                    fee_amount_granted money not null with default);

       create table attendance_detail(
                    ad_student_id integer, 
                    ad_absence_tardy char(1), 
                    ad_date date,
                    ad_excused_unexcus varchar(10), 
                    ad_teacher varchar(30), 
                    ad_period integer);

       EXECUTE IMMEDIATE
       'create table student ' + '( ' +
                     'student_id integer, ' +
                     'student_name varchar(30), ' +
                     'student_loc_id integer, ' +
                     'student_picture vachar(76) ' + ')';

       EXECUTE IMMEDIATE
       'create unique clustered index student_idx ' +
       ' on student (student_name)';

       EXECUTE IMMEDIATE
       'create table student_images ' + '( ' +
                      'picture_id integer not null, ' +
                      'row_sequence integer not null, ' +
                      'text_total integer not null, ' +
                      'text_value varchar(255) not null ' + ')';

       EXECUTE IMMEDIATE
       'create unique clustered index student_images_idx ' +
       'on student_images (picture_id, row_sequence)';

    endif;

    callproc do_grant_and_synonym(name = 'student');
    callproc do_grant_and_synonym(name = 'student_images');
    callproc do_grant_and_synonym(name = 'student_info');
    callproc do_grant_and_synonym(name = 'attendance_detail');
    callproc do_grant_and_synonym(name = 'location');
    callproc do_grant_and_synonym(name = 'fees');
    callproc do_grant_and_synonym(name = 'grades');
    callproc do_grant_and_synonym(name = 'parent_info');

/****************************************/
/* turn autocommit off for InsertIntoDB */
/****************************************/
commit;

CurProcedure.DBSession.SetAutoCommit(state = FALSE);

    bo.FileHandle = BmFilePath + 'sis1.bmp';
    status = bo.InsertIntoDB(tablename = 'student_images');
    if status = ER_OK then
        insert into student values (10031, 'Jones, Ann', 54, bo.dbhandle);
       endif;
     commit;

    bo.FileHandle = BmFilePath+ 'sis2.bmp';
    status = bo.InsertIntoDB(tablename = 'student_images');
    if status = ER_OK then
       insert into student values (10001, 'Smith, Mary', 12, bo.dbhandle);
       endif;
    commit;

    bo.FileHandle = BmFilePath + 'sis3.bmp';
    status = bo.InsertIntoDB(tablename = 'student_images');
    if status = ER_OK then
       insert into student values (23359, 'Reed, Ron', 18, bo.dbhandle);
       endif;
    commit;

    bo.FileHandle = BmFilePath + 'sis4.bmp';
    status = bo.InsertIntoDB(tablename = 'student_images');
    if status = ER_OK then
       insert into student values (13324, 'Cook, Dan', 54, bo.dbhandle);
       endif;
    commit;

    bo.FileHandle = BmFilePath + 'sis5.bmp';
    status = bo.InsertIntoDB(tablename = 'student_images');
    if status = ER_OK then
        insert into student values (55678, 'King, Jim', 12, bo.dbhandle);
        endif;
    commit;

    bo.FileHandle = BmFilePath + 'sis6.bmp';
    status = bo.InsertIntoDB(tablename = 'student_images');
    if status = ER_OK then
       insert into student values (98889, 'Hill, Jan', 18, bo.dbhandle);
       endif;
    commit;

/***************************/
/* turn autocommit back on */
/***************************/

CurProcedure.DBSession.SetAutoCommit(state = TRUE);

insert into attendance_detail values(10001,'A',date('05-may-1996'),'Excused','Bob Brindell',3);

