procedure buildarea
( 
) =
declare
    tablename	= varchar(24)not null;
    ret_stat	= smallint not null;
    servertype	= integer not null;
enddeclare
begin
    tablename = 'area' ;
    ret_stat = Callproc CkTableExists(param_tbl = :tablename);
    if ret_stat = TableFound then
	Drop table area; 
    endif;

    servertype = CurProcedure.DBSession.ServerType;

    if (servertype = DS_INGRES_DBMS) then

	create table area(
	    region_id		char(2)		not null with default,
	    area_id		char(4)		not null with default,
	    area_desc		varchar(20)	not null with default
	)
	with noduplicates;

	if (NativeIngres = TRUE) then

	    modify area to btree unique on region_id, area_id
	        with nonleaffill = 80, leaffill = 70, fillfactor = 80;

	else

	    create unique index area_mod on area (region_id, area_id);

	endif;

    elseif (servertype = DS_INGRESDSK_DBMS) then

	EXECUTE IMMEDIATE
	'create table area ' +
	'( ' +
	    'region_id		char(2)		not null with default, ' +
	    'area_id		char(4)		not null with default, ' +
	    'area_desc		varchar(20)	not null with default, ' +

	    'PRIMARY KEY (region_id, area_id) ' +
	')';

	EXECUTE IMMEDIATE
	'create unique index area_mod ' +
	'on area (region_id, area_id)';

    elseif (servertype = DS_ORACLE_DBMS) then

	EXECUTE IMMEDIATE
	'create table area ' +
	'( ' +
	    'region_id		char(2)		not null, ' +
	    'area_id		char(4)		not null, ' +
	    'area_desc		varchar(20)	not null, ' +
	    'CONSTRAINT area_pk '		+
	    'PRIMARY KEY (region_id, area_id) ' +
	')';

    elseif (servertype = DS_SQLSERVER_DBMS) then

	EXECUTE IMMEDIATE
	'create table area ' +
	'( ' +
	    'region_id		char(2)		not null, ' +
	    'area_id		char(4)		not null, ' +
	    'area_desc		varchar(20)	not null ' +
	')';

	EXECUTE IMMEDIATE
	'create unique clustered index area_mod ' +
	'on area (region_id, area_id)';

    endif;

    callproc do_grant_and_synonym(name = 'area');


insert into area values ('NE',   'BOSS', 'Metro Boston'        );
insert into area values ('NE',   'DC'  , 'District of Colombia');
insert into area values ('NE',   'NY'  , 'New York'            );
insert into area values ('NE',   'NYCM', 'NYC Metro'           );
insert into area values ('NE',   'PHIL', 'Philadelphia'        );
insert into area values ('NE',   'PITT', 'Pittsburg'           );

end

