**  History:
**
**	11-oct-95 (fraser)
**	    DBI change.  Added code to create tables for OpenIngres/Desktop.
**	    Removed commit statements which are unnecessary because this
**	    procedure is now run in autocommit mode.  Removed dollar signs
**	    from money values in insert statements because they're not 
**	    necessary for Ingres and they aren't accepted by OpenIngres/Desktop.
**	26-oct-95 (fraser)
**	    Added code for Oracle and SQL Server.
**	27-oct-95 (fraser)
**	    Removed single quotes from money values in insert statements
**	    because SQL Server does not provide implicit converison from
**	    character types to money.
**	15-feb-96 (fraser)
**	    Oracle: Changed model_id to varchar2.
**	07-nov-96 (sandyd)
**	    Split DS_INGRES_DBMS case into NativeIngres versus gateway SQL.
*/

procedure buildavailable_packages
( 
) =
declare
    tablename	= varchar(24)not null;
    ret_stat	= smallint not null;
    servertype	= integer not null;
enddeclare
begin
    tablename = 'available_packages' ;
    ret_stat = Callproc CkTableExists(param_tbl = :tablename);
    if ret_stat = TableFound then
	Drop table available_packages;
    endif;

    servertype = CurProcedure.DBSession.ServerType;

    if (servertype = DS_INGRES_DBMS) then

	if (NativeIngres = TRUE) then

	    create table available_packages(
		model_id	char(8)		not null,
		package_id	char(8)		not null,
		package_base	char(1)		not null with default,
		package_desc	varchar(20)	not null with default,
		package_price	money		not null with default
	    )
	    with noduplicates;

	    modify available_packages to btree unique on model_id, package_id
		with nonleaffill = 80, leaffill = 70, fillfactor = 80;

	else

	    create table available_packages(
		model_id	char(8)		not null,
		package_id	char(8)		not null,
		package_base	char(1)		not null with default,
		package_desc	varchar(20)	not null with default,
		package_price	float /*money*/ not null with default
	    );

	    create unique index available_pack_mod on available_packages
		(model_id, package_id);

	endif;

    elseif (servertype = DS_INGRESDSK_DBMS) then

	EXECUTE IMMEDIATE
	'create table available_packages ' +
	'( ' +
	    'model_id		char(8)		not null, '	+
	    'package_id		char(8)		not null, '	+
	    'package_base	char(1)		not null with default, ' +
	    'package_desc	varchar(20)	not null with default, ' +
	    'package_price	decimal(14,2), '  +

	    'PRIMARY KEY (model_id, package_id) ' +
	')';

	EXECUTE IMMEDIATE
	'create unique index available_pack_mod ' +
	'on available_packages (model_id, package_id)';

    elseif (servertype = DS_ORACLE_DBMS) then

	EXECUTE IMMEDIATE
	'create table available_packages ' +
	'( ' +
	    'model_id		varchar2(8)	not null, ' +
	    'package_id		char(8)		not null, ' +
	    'package_base	char(1)		not null, ' +
	    'package_desc	varchar(20)	not null, ' +
	    'package_price	number(14,2), '		    +
	    'CONSTRAINT available_pack_pk '	  +
	    'PRIMARY KEY (model_id, package_id) ' +
	')';

    elseif (servertype = DS_SQLSERVER_DBMS) then

	EXECUTE IMMEDIATE
	'create table available_packages ' +
	'( ' +
	    'model_id		char(8)		not null, ' +
	    'package_id		char(8)		not null, ' +
	    'package_base	char(1)		not null, ' +
	    'package_desc	varchar(20)	not null, ' +
	    'package_price	money '	    +
	')';

	EXECUTE IMMEDIATE
	'create unique clustered index available_pack_mod ' +
	'on available_packages (model_id, package_id)';

    endif;

    callproc do_grant_and_synonym(name = 'available_packages');

