procedure buildcalendar ( ) = declare tablename = varchar(24)not null; ret_stat = smallint not null; enddeclare begin tablename = 'calendartab' ; ret_stat = Callproc CkTableExists(param_tbl = :tablename); if ret_stat = TableFound then Drop table calendartab; commit; endif;
if ( CurProcedure.DBSession.ServerType = DS_INGRES_DBMS ) then
create table calendartab ( dat date not null, usname varchar(40) not null, one varchar(40) not null, two varchar(40) not null, three varchar(40) not null, four varchar(40) not null, five varchar(40) not null, six varchar(40) not null, seven varchar(40) not null, eight varchar(40) not null, nine varchar(40) not null, ten varchar(40) not null, eleven varchar(40) not null, twelve varchar(40) not null, thirteen varchar(40) not null, fourteen varchar(40) not null, fifteen varchar(40) not null, sixteen varchar(40) not null, seventeen varchar(40) not null, eightteen varchar(40) not null, nineteen varchar(40) not null, twenty varchar(40) not null, twentyone varchar(40) not null, twentytwo varchar(40) not null, twentythree varchar(40) not null, twentyfour varchar(40) not null ) with duplicates;
if (NativeIngres = TRUE) then
modify calendartab to cheap;
endif;
elseif ( CurProcedure.DBSession.ServerType = DS_INGRESDSK_DBMS ) then
EXECUTE IMMEDIATE 'create table calendartab ' + '( ' + 'dat datetime not null, ' + 'usname varchar(40) not null, ' + 'one varchar(40) not null, ' + 'two varchar(40) not null, ' + 'three varchar(40) not null, ' + 'four varchar(40) not null, ' + 'five varchar(40) not null, ' + 'six varchar(40) not null, ' + 'seven varchar(40) not null, ' + 'eight varchar(40) not null, ' + 'nine varchar(40) not null, ' + 'ten varchar(40) not null, ' + 'eleven varchar(40) not null, ' + 'twelve varchar(40) not null, ' + 'thirteen varchar(40) not null, ' + 'fourteen varchar(40) not null, ' + 'fifteen varchar(40) not null, ' + 'sixteen varchar(40) not null, ' + 'seventeen varchar(40) not null, ' + 'eightteen varchar(40) not null, ' + 'nineteen varchar(40) not null, ' + 'twenty varchar(40) not null, ' + 'twentyone varchar(40) not null, ' + 'twentytwo varchar(40) not null, ' + 'twentythree varchar(40) not null, ' + 'twentyfour varchar(40) not null ' + ')';
elseif ( CurProcedure.DBSession.ServerType = DS_ORACLE_DBMS ) then
EXECUTE IMMEDIATE 'create table calendartab ' + '( ' + 'dat date constraint nn_dat not null, ' + 'usname varchar(40) constraint nn_usname not null, ' + 'one varchar(40) constraint nn_one not null, ' + 'two varchar(40) constraint nn_two not null, ' + 'three varchar(40) constraint nn_three not null, ' + 'four varchar(40) constraint nn_four not null, ' + 'five varchar(40) constraint nn_five not null, ' + 'six varchar(40) constraint nn_six not null, ' + 'seven varchar(40) constraint nn_seven not null, ' + 'eight varchar(40) constraint nn_eight not null, ' + 'nine varchar(40) constraint nn_nine not null, ' + 'ten varchar(40) constraint nn_ten not null, ' + 'eleven varchar(40) constraint nn_eleven not null, ' + 'twelve varchar(40) constraint nn_twelve not null, ' + 'thirteen varchar(40) constraint nn_thirteen not null, ' + 'fourteen varchar(40) constraint nn_fourteen not null, ' + 'fifteen varchar(40) constraint nn_fifteen not null, ' + 'sixteen varchar(40) constraint nn_sixteen not null, ' + 'seventeen varchar(40) constraint nn_seventeen not null, ' + 'eightteen varchar(40) constraint nn_eightteen not null, ' + 'nineteen varchar(40) constraint nn_nineteen not null, ' + 'twenty varchar(40) constraint nn_twenty not null, ' + 'twentyone varchar(40) constraint nn_twentyone not null, ' + 'twentytwo varchar(40) constraint nn_twentytwo not null, ' + 'twentythree varchar(40) constraint nn_twentythree not null, ' + 'twentyfour varchar(40) constraint nn_twentyfour not null ' + ')';
elseif ( CurProcedure.DBSession.ServerType = DS_SQLSERVER_DBMS ) then
EXECUTE IMMEDIATE 'create table calendartab ' + '( ' + 'dat datetime not null, ' + 'usname varchar(40) not null, ' + 'one varchar(40) not null, ' + 'two varchar(40) not null, ' + 'three varchar(40) not null, ' + 'four varchar(40) not null, ' + 'five varchar(40) not null, ' + 'six varchar(40) not null, ' + 'seven varchar(40) not null, ' + 'eight varchar(40) not null, ' + 'nine varchar(40) not null, ' + 'ten varchar(40) not null, ' + 'eleven varchar(40) not null, ' + 'twelve varchar(40) not null, ' + 'thirteen varchar(40) not null, ' + 'fourteen varchar(40) not null, ' + 'fifteen varchar(40) not null, ' + 'sixteen varchar(40) not null, ' + 'seventeen varchar(40) not null, ' + 'eightteen varchar(40) not null, ' + 'nineteen varchar(40) not null, ' + 'twenty varchar(40) not null, ' + 'twentyone varchar(40) not null, ' + 'twentytwo varchar(40) not null, ' + 'twentythree varchar(40) not null, ' + 'twentyfour varchar(40) not null ' + ')';
