procedure buildcars_equip
( 
) =
declare
    tablename	= varchar(24) not null;
    ret_stat	= smallint not null;
    servertype	= integer not null;
enddeclare
begin
    tablename = 'cars_equip' ;
    ret_stat = Callproc CkTableExists(param_tbl = :tablename);
    if ret_stat = TableFound then
	Drop table cars_equip; 
    endif;

    servertype = CurProcedure.DBSession.ServerType;

    if (servertype = DS_INGRES_DBMS) then

	create table cars_equip(
	    vin		varchar(20)	not null,
	    package_id	char(8)		not null
	)
	with duplicates;

	if (NativeIngres = TRUE) then

	    modify cars_equip to hash on vin
		with minpages = 10, fillfactor = 50;

	else

	    create index cars_equip_mod on cars_equip (vin);

	endif;

    elseif (servertype = DS_INGRESDSK_DBMS) then

	EXECUTE IMMEDIATE
	'create table cars_equip ' +
	'( ' +
	    'vin		varchar(20)	not null, ' +
	    'package_id		char(8)		not null ' +
	')';

    elseif (servertype = DS_ORACLE_DBMS) then

	EXECUTE IMMEDIATE
	'create table cars_equip ' +
	'( ' +
	    'vin		varchar(20)	not null, ' +
	    'package_id		char(8)		not null ' +
	')';

    elseif (servertype = DS_SQLSERVER_DBMS) then

	EXECUTE IMMEDIATE
	'create table cars_equip ' +
	'( ' +
	    'vin		varchar(20)	not null, ' +
	    'package_id		char(8)		not null ' +
	')';

    endif;

    callproc do_grant_and_synonym(name = 'cars_equip');

insert into cars_equip values('F54608154278JM87', 'BS_FBCP');

