procedure buildcountry
( 
) =
declare
    tablename	= varchar(24) not null;
    ret_stat	= smallint not null;
    servertype	= integer not null;
enddeclare
begin
    tablename = 'country';
    ret_stat = Callproc CkTableExists(param_tbl = :tablename);
    if ret_stat = TableFound then 
	Drop table country;
    endif;

    servertype = CurProcedure.DBSession.ServerType;

    if (servertype = DS_INGRES_DBMS) then

	create table country(
	    country_code	char(3)		not null with default,
	    country_desc	varchar(20)	not null with default,
	    manufacturer	char(1)		not null with default
	)
	with noduplicates;

	if (NativeIngres = TRUE) then

	    modify country to btree unique on country_code
		with nonleaffill = 80, leaffill = 70, fillfactor = 80;

	else

	    create unique index country_mod on country (country_code);

	endif;

    elseif (servertype = DS_INGRESDSK_DBMS) then

	EXECUTE IMMEDIATE
	'create table country ' +
	'( ' +
	    'country_code	char(3)		not null with default, ' +
	    'country_desc	varchar(20)	not null with default, ' +
	    'manufacturer	char(1)		not null with default, ' +

	    'PRIMARY KEY (country_code) ' +
	') ';

	EXECUTE IMMEDIATE
	'create unique index country_mod ' +
	'on country (country_code)';

    elseif (servertype = DS_ORACLE_DBMS) then

	EXECUTE IMMEDIATE
	'create table country ' +
	'( ' +
	    'country_code	char(3)		not null, ' +
	    'country_desc	varchar(20)	not null, ' +
	    'manufacturer	char(1)		not null, ' +
	    'CONSTRAINT country_pk '	  +
	    'PRIMARY KEY (country_code) ' +
	') ';

    elseif (servertype = DS_SQLSERVER_DBMS) then

	EXECUTE IMMEDIATE
	'create table country ' +
	'( ' +
	    'country_code	char(3)		not null, ' +
	    'country_desc	varchar(20)	not null, ' +
	    'manufacturer	char(1)		not null ' +
	') ';

	EXECUTE IMMEDIATE
	'create unique clustered index country_mod ' +
	'on country (country_code)';

    endif;

    callproc do_grant_and_synonym(name = 'country');

insert into country values ('CN' ,  'Canada'        ,  'N');
insert into country values ('FR' ,  'France'        ,  'N');
insert into country values ('GM' ,  'Germany'       ,  'Y');
insert into country values ('JA' ,  'Japan'         ,  'Y');
insert into country values ('NA' ,  'North America' ,  'Y');
insert into country values ('SW' ,  'Sweden'        ,  'Y');
insert into country values ('UK' ,  'United Kingdom',  'Y');
insert into country values ('USA',  'USA'           ,  'N');

end

