**	07-nov-96 (sandyd)
**	    Adapted DS_INGRES_DBMS case to work on gateways and NativeIngres.
*/

procedure buildcustomer
( 
) =
declare
    tablename	= varchar(24) not null;
    ret_stat	= smallint not null;
    servertype	= integer not null;
enddeclare
begin
    tablename = 'customer' ;
    ret_stat = Callproc CkTableExists(param_tbl = :tablename);
    if ret_stat = TableFound then 
	Drop table customer;
    endif;

    servertype = CurProcedure.DBSession.ServerType;

    if (servertype = DS_INGRES_DBMS) then

 	create table customer(
	    customer_id		integer		not null,
	    customer_name	varchar(41)	with null,
	    address		varchar(30)	with null,
	    city		varchar(20)	with null,
	    state		char(5)		with null,
	    zip			varchar(9)	with null,
	    phone		varchar(14)	not null with default
	)
	with duplicates;

	if (NativeIngres = TRUE) then

	    modify customer to btree unique on customer_id
		with nonleaffill = 80, leaffill = 70, fillfactor = 80;

	else

	    create unique index customer_mod on customer (customer_id);

	endif;

    elseif (servertype = DS_INGRESDSK_DBMS) then

	EXECUTE IMMEDIATE
	'create table customer ' +
	'( ' +
	    'customer_id	integer		not null, '	+
	    'customer_name	varchar(41),'			+
	    'address		varchar(30), '			+
	    'city		varchar(20), '			+
	    'state		char(5), '			+
	    'zip		varchar(9), '			+
	    'phone		varchar(14)	not null with default, ' +

	    'PRIMARY KEY (customer_id) '	+
	') ';

	EXECUTE IMMEDIATE
	'create unique index customer_mod ' +
	'on customer (customer_id)';

    elseif (servertype = DS_ORACLE_DBMS) then

	EXECUTE IMMEDIATE
	'create table customer ' +
	'( ' +
	    'customer_id	integer		not null, '	+
	    'customer_name	varchar(41),'			+
	    'address		varchar(30), '			+
	    'city		varchar(20), '			+
	    'state		char(5), '			+
	    'zip		varchar(9), '			+
	    'phone		varchar(14)	not null, ' +
	    'CONSTRAINT customer_id_pk '	+
	    'PRIMARY KEY (customer_id) '	+
	') ';

    elseif (servertype = DS_SQLSERVER_DBMS) then

	EXECUTE IMMEDIATE
	'create table customer ' +
	'( ' +
	    'customer_id	int		not null, '	+
	    'customer_name	varchar(41),'			+
	    'address		varchar(30), '			+
	    'city		varchar(20), '			+
	    'state		char(5), '			+
	    'zip		varchar(9), '			+
	    'phone		varchar(14)	not null ' +
	') ';

	EXECUTE IMMEDIATE
	'create unique clustered index customer_mod ' +
	'on customer (customer_id)';

    endif;

    callproc do_grant_and_synonym(name = 'customer');

insert into customer values (  1, 'Peter Janik'       ,  '555 Santa Ana Street'      , 'Orange'          , 'CA',  '91711'      , '7555551150' );
