/* ** History: ** 12-oct-95 (clays) ** Added code to create tables for Ingres/Desktop and ** removed unnecessary commits since run in autocommit mode. ** 18-oct-95 (clays) ** Added code to create tables for Oracle DBMS. ** 23-oct-95 (clays) ** Added code to create tables for SQL Server DBMS. ** 07-nov-96 (sandyd) ** Split DS_INGRES_DBMS case into NativeIngres versus gateway SQL. ** */
procedure buildcustomers ( ) = declare tablename = varchar(24) not null; ret_stat = smallint not null; enddeclare begin tablename = 'customers' ; ret_stat = Callproc CkTableExists(param_tbl = :tablename); if ret_stat = TableFound then drop table customers; endif;
if ( CurProcedure.DBSession.ServerType = DS_INGRES_DBMS ) then
if (NativeIngres = TRUE) then
create table customers ( cust_no integer not null, name char(20) not null, street char(20) not null, city char(20), state char(2), discount float, balance money ) with noduplicates;
modify customers to btree unique on cust_no with nonleaffill = 80, leaffill = 70, fillfactor = 80;
else
create table customers ( cust_no integer not null, name char(20) not null, street char(20) not null, city char(20), state char(2), discount float, balance float /*money*/ );
create unique index customers_mod on customers (cust_no);
endif;
elseif ( CurProcedure.DBSession.ServerType = DS_INGRESDSK_DBMS ) then
EXECUTE IMMEDIATE 'create table customers ' + '( ' + ' cust_no integer not null, ' + ' name char(20) not null, ' + ' street char(20) not null, ' + ' city char(20), ' + ' state char(2), ' + ' discount double precision, ' + ' balance decimal(14,2), ' +
' PRIMARY KEY( cust_no )' + ')';
EXECUTE IMMEDIATE 'create unique index customers_mod ' + 'on customers( cust_no )';
elseif ( CurProcedure.DBSession.ServerType = DS_ORACLE_DBMS ) then
EXECUTE IMMEDIATE 'create table customers ' + '( ' + ' cust_no integer constraint nn_cust_no not null, ' + ' name char(20) constraint nn_name not null, ' + ' street char(20) constraint nn_street not null, ' + ' city char(20), ' + ' state char(2), ' + ' discount double precision, ' + ' balance decimal(14,2), ' +
' constraint pk_customers ' + ' UNIQUE( cust_no ) ' + ')';
elseif ( CurProcedure.DBSession.ServerType = DS_SQLSERVER_DBMS ) then
EXECUTE IMMEDIATE 'create table customers ' + '( ' + ' cust_no int not null, ' + ' name char(20) not null, ' + ' street char(20) not null, ' + ' city char(20) null, ' + ' state char(2) null, ' + ' discount float null, ' + ' balance money null ' + ')';
EXECUTE IMMEDIATE 'create unique index customers_mod ' + 'on customers( cust_no ) ' + 'with fillfactor = 80 ';
endif;
CALLPROC do_grant_and_synonym( name = 'customers' );
insert into customers (cust_no, name, street, city, state, discount, balance) values( 101, 'Stand Brands','10002 San Pablo','El Cerrito', 'Ca',0.110,5000.00);
