PROCEDURE BuildDescriptorArrays()=
DECLARE
    RowNumber = INTEGER NOT NULL;
    Status = INTEGER NOT NULL;
    i = INTEGER NOT NULL;
    j = INTEGER NOT NULL;

    BuildTableDescriptorArray = PROCEDURE;
    RegisterCustomer_order = PROCEDURE;
    RegisterVideos = PROCEDURE;
    RegisterMotors = PROCEDURE;
    RegisterSIS = PROCEDURE;
ENDDECLARE
BEGIN
    i = 0;
    CALLPROC BuildTableDescriptorArray();

    i = 0;
    CALLPROC RegisterVideos();
    //CALLPROC RegisterSIS();

    AppDescriptorArray.Sort(Name = AS_ASC);
END;

PROCEDURE RegisterSIS()=
BEGIN
    i = i + 1;
    j = 1;

    AppDescriptorArray[i].Name = 'Student_Information_System';
    AppDescriptorArray[i].IsLoaded = FALSE;
    AppDescriptorArray[i].IsRunning = FALSE;
    AppDescriptorArray[i].FileName = 'sis.exp';

    Status = TableDescriptorArray.Find(AttributeName = 'Name',
                                       Value = 'sis_data',
                                       RowNumber = BYREF(RowNumber));
    $_SetTableDescriptor

    AppDescriptorArray[i].TableArray.Sort(Name = AS_ASC);
END

PROCEDURE RegisterCustomer_order()=
BEGIN
    i = i + 1;
    j = 1;

    AppDescriptorArray[i].Name = 'Customer_order';
    AppDescriptorArray[i].IsLoaded = FALSE;
    AppDescriptorArray[i].IsRunning = FALSE;
    AppDescriptorArray[i].FileName = 'custordr.exp';

    Status = TableDescriptorArray.Find(AttributeName = 'Name',
                                       Value = 'customers',
                                       RowNumber = BYREF(RowNumber));
    $_SetTableDescriptor

    Status = TableDescriptorArray.Find(AttributeName = 'Name',
                                       Value = 'orders',
                                       RowNumber = BYREF(RowNumber));
    $_SetTableDescriptor

    Status = TableDescriptorArray.Find(AttributeName = 'Name',
                                       Value = 'order_items',
                                       RowNumber = BYREF(RowNumber));
    $_SetTableDescriptor

    Status = TableDescriptorArray.Find(AttributeName = 'Name',
                                       Value = 'calendartab',
                                       RowNumber = BYREF(RowNumber));
    $_SetTableDescriptor

    Status = TableDescriptorArray.Find(AttributeName = 'Name',
                                       Value = 'part',
                                       RowNumber = BYREF(RowNumber));
    $_SetTableDescriptor

    AppDescriptorArray[i].TableArray.Sort(Name = AS_ASC);
END

