			/*
**  History;
**
**	11-oct-95 (fraser)
**	    DBI change.  Added code to build tables for OpenIngres/Desktop.
**	    Removed commit statements which are unnecessary because this
**	    procedure is now run in autocommit mode.
**	26-oct-95 (fraser)
**	    Added code for Oracle and SQL Server.
**	07-nov-96 (sandyd)
**	    Adapted DS_INGRES_DBMS case to work on gateways and NativeIngres.
*/

procedure buildemp_class
( 
) =
declare
    tablename	= varchar(24) not null;
    ret_stat	= smallint not null;
    servertype	= integer not null;
enddeclare
begin
    tablename = 'emp_class' ;
    ret_stat = Callproc CkTableExists(param_tbl = :tablename);
    if ret_stat = TableFound then 
	Drop table emp_class;
    endif;

    servertype = CurProcedure.DBSession.ServerType;

    if (servertype = DS_INGRES_DBMS) then

	create table emp_class(
	    class_code		char(2)		not null with default,
	    class_desc		varchar(20)	not null with default
	)
	with noduplicates;

	if (NativeIngres = TRUE) then

	    modify emp_class to btree unique on class_code
		with nonleaffill = 80, leaffill = 70, fillfactor = 80;

	else

	    create unique index emp_class_mod on emp_class (class_code);

	endif;

    elseif (servertype = DS_INGRESDSK_DBMS) then

	EXECUTE IMMEDIATE
	'create table emp_class ' +
	'( ' +
	    'class_code		char(2)		not null with default, ' +
	    'class_desc		varchar(20)	not null with default, ' +

	    'PRIMARY KEY (class_code) ' +
	')';

	EXECUTE IMMEDIATE
	'create unique index emp_class_mod ' +
	'on emp_class (class_code)';

    elseif (servertype = DS_ORACLE_DBMS) then

	EXECUTE IMMEDIATE
	'create table emp_class ' +
	'( ' +
	    'class_code		char(2)		not null, ' +
	    'class_desc		varchar(20)	not null, ' +
	    'CONSTRAINT emp_class_pk '	+
	    'PRIMARY KEY (class_code) ' +
	')';

    elseif (servertype = DS_SQLSERVER_DBMS) then

	EXECUTE IMMEDIATE
	'create table emp_class ' +
	'( ' +
	    'class_code		char(2)		not null, ' +
	    'class_desc		varchar(20)	not null ' +
	')';

	EXECUTE IMMEDIATE
	'create unique clustered index emp_class_mod ' +
	'on emp_class (class_code)';

    endif;

    callproc do_grant_and_synonym(name = 'emp_class');

insert into emp_class values ('DO', 'Dealer Owner'      );
insert into emp_class values ('FM', 'Finance Manager'   );
insert into emp_class values ('SM', 'Sales Manager'     );
insert into emp_class values ('SR', 'Sales Rep'         );
insert into emp_class values ('TE', 'Technician'        );
insert into emp_class values ('TM', 'Technical Manager' );

end

