procedure buildemployee
( 
) =
declare
    tablename	= varchar(24) not null;
    ret_stat	= smallint not null;
    servertype	= integer not null;
enddeclare
begin
    tablename = 'employee' ;
    ret_stat = Callproc CkTableExists(param_tbl = :tablename);
    if ret_stat = TableFound then
	Drop table employee;
    endif;

    servertype = CurProcedure.DBSession.ServerType;

    if (servertype = DS_INGRES_DBMS) then

	create table employee(
	    employee_nbr	char(5)		not null,
	    loc_code		integer		not null,
	    class_code		char(2)		not null,
	    sec_code		smallint	not null,
	    lastname		varchar(30)	not null,
	    firstname		varchar(20)	not null with default
	)
	with duplicates;

	if (NativeIngres = TRUE) then

	    modify employee to btree unique on employee_nbr
		with nonleaffill = 80, leaffill = 70, fillfactor = 80;

	else

	    create unique index employee_mod on employee (employee_nbr);

	endif;

    elseif (servertype = DS_INGRESDSK_DBMS) then

	EXECUTE IMMEDIATE
	'create table employee ' +
	'( ' +
	    'employee_nbr	char(5)		not null, ' +
	    'loc_code		integer		not null, ' +
	    'class_code		char(2)		not null, ' +
	    'sec_code		smallint	not null, ' +
	    'lastname		varchar(30)	not null, ' +
	    'firstname		varchar(20)	not null with default, ' +

	    'PRIMARY KEY (employee_nbr)' +
	')';

	EXECUTE IMMEDIATE
	'create unique index employee_mod ' +
	'on employee (employee_nbr)';

    elseif (servertype = DS_ORACLE_DBMS) then

	EXECUTE IMMEDIATE
	'create table employee ' +
	'( ' +
	    'employee_nbr	char(5)		not null, ' +
	    'loc_code		integer		not null, ' +
	    'class_code		char(2)		not null, ' +
	    'sec_code		smallint	not null, ' +
	    'lastname		varchar(30)	not null, ' +
	    'firstname		varchar(20)	not null, ' +
	    'CONSTRAINT employee_pk '	 +
	    'PRIMARY KEY (employee_nbr)' +
	')';

    elseif (servertype = DS_SQLSERVER_DBMS) then

	EXECUTE IMMEDIATE
	'create table employee ' +
	'( ' +
	    'employee_nbr	char(5)		not null, ' +
	    'loc_code		int		not null, ' +
	    'class_code		char(2)		not null, ' +
	    'sec_code		smallint	not null, ' +
	    'lastname		varchar(30)	not null, ' +
	    'firstname		varchar(20)	not null ' +
	')';

	EXECUTE IMMEDIATE
	'create unique clustered index employee_mod ' +
	'on employee (employee_nbr)';

    endif;

    callproc do_grant_and_synonym(name = 'employee');

insert into employee values('11111', 120, 'DO', 100, 'Simpson', 'Maggie');

insert into employee values('12749', 100, 'DO',   1, 'Regal', 'Joseph');

insert into employee values('22222', 120, 'DO', 100, 'Simpson', 'Lisa');

insert into employee values('33333', 120, 'DO', 100, 'Simpson', 'Homer');

insert into employee values('38927', 100, 'SR',   1, 'Smith', 'John');

