procedure buildfixed_features
( 
) =
declare
    tablename	= varchar(24) not null;
    ret_stat	= smallint not null;
    servertype	= integer not null;
enddeclare
begin
    tablename = 'fixed_features' ;
    ret_stat = Callproc CkTableExists(param_tbl = :tablename);
    if ret_stat = TableFound then
	Drop table fixed_features; 
    endif;

    servertype = CurProcedure.DBSession.ServerType;

    if (servertype = DS_INGRES_DBMS) then

	create table fixed_features(
	    model_id		char(8)		not null with default,
	    feature_desc	varchar(80)	not null with default
	)
	with noduplicates;

	if (NativeIngres = TRUE) then

	    modify fixed_features to btree unique on model_id
		with nonleaffill = 80, leaffill = 70, fillfactor = 80;

	else

	    create unique index fixed_features_mod on fixed_features (model_id);

	endif;

    elseif (servertype = DS_INGRESDSK_DBMS) then

	EXECUTE IMMEDIATE
	'create table fixed_features ' +
	'( ' +
	    'model_id		char(8)		not null with default, ' +
	    'feature_desc	varchar(80)	not null with default, ' +

	    'PRIMARY KEY (model_id)' +
	')';

	EXECUTE IMMEDIATE
	'create unique index fixed_features_mod ' +
	'on fixed_features (model_id)';

    elseif (servertype = DS_ORACLE_DBMS) then

	EXECUTE IMMEDIATE
	'create table fixed_features ' +
	'( ' +
	    'model_id		varchar2(8)	not null, ' +
	    'feature_desc	varchar(80)	not null, ' +
	    'CONSTRAINT fixed_features_pk '	+
	    'PRIMARY KEY (model_id)'		+
	')';

    elseif (servertype = DS_SQLSERVER_DBMS) then

	EXECUTE IMMEDIATE
	'create table fixed_features ' +
	'( ' +
	    'model_id		char(8)		not null, ' +
	    'feature_desc	varchar(80)	not null ' +
	')';

	EXECUTE IMMEDIATE
	'create unique clustered index fixed_features_mod ' +
	'on fixed_features (model_id)';

    endif;

    callproc do_grant_and_synonym(name = 'fixed_features');


