			/*
**  History:
**
**	11-oct-95 (fraser)
**	    DBI change.  Added code to build tables for OpenIngres/Desktop.
**	    Removed commit statements which are no longer needed because this
**	    procedure is now run in autocommit mode.  Removed '\' from
**	    BmFilePath for Unix portability.
**	13-oct-95 (fraser)
**	    Use global variable demo-directory to set BmFilePath.
**	14-oct-95 (fraser)
**	    Turn autocommit off during inserts.  The InsertIntoDB methods
**	    of BitMapObject and StringObject don't work for OID unless
**	    autocommit is off.
**	26-oct-95 (fraser)
**	    Added code for Oracle and SQL Server.
**	16-jan-96 (judyv) changed bitmap filenames from .bmp to .gif
**          this should have been done for bug 72414.
**	06-feb-96 (judyv) #73882
**	    Added commit to insert statements to prevent logfull condition.
**	15-feb-96 (fraser)
**	    Oracle change: To avoid problem comparing a varchar variable
**	    to a char column, changed manufacturer_id to varchar2.
**	07-nov-96 (sandyd)
**	    Adapted DS_INGRES_DBMS case to work on gateways and NativeIngres.
*/

procedure buildmanufacturer
( 
) =
declare
    bo		= BitmapObject;
    logo_dbh	= varchar(76) not null with default;
    logo2_dbh	= varchar(76) not null with default;
    BmFilePath	= varchar(256)not null;
    status	= integer not null;

    tablename	= varchar(24) not null;
    ret_stat	= smallint not null;
    servertype	= integer not null;
enddeclare
begin
    BmFilePath = demo_directory;
    tablename = 'manufacturer' ;
    ret_stat = Callproc CkTableExists(param_tbl = :tablename);
    if ret_stat = TableFound then 
	Drop table manufacturer;
    endif;

    servertype = CurProcedure.DBSession.ServerType;

    if (servertype = DS_INGRES_DBMS) then

	create table manufacturer(
	    manufacturer_id	char(8)		not null,
	    manufacturer_desc	varchar(15)	not null with default,
	    country_code	char(3)		not null with default,
	    logo_handle		varchar(76)	not null,
	    logo_select_handle	varchar(76)	not null
	)
	with noduplicates;

	if (NativeIngres = TRUE) then

	    modify manufacturer to btree unique on manufacturer_id
		with nonleaffill = 80, leaffill = 70, fillfactor = 80;

	else

	    create unique index manufacturer_mod on manufacturer
		(manufacturer_id);

	endif;

    elseif (servertype = DS_INGRESDSK_DBMS) then

	EXECUTE IMMEDIATE
	'create table manufacturer ' +
	'( ' +
	    'manufacturer_id	char(8)		not null, ' +
	    'manufacturer_desc	varchar(15)	not null with default, ' +
	    'country_code	char(3)		not null with default, ' +
	    'logo_handle	varchar(76)	not null, ' +
	    'logo_select_handle	varchar(76)	not null, ' +

	    'PRIMARY KEY (manufacturer_id) ' +
	')';

	EXECUTE IMMEDIATE
	'create unique index manufacturer_mod ' +
	'on manufacturer (manufacturer_id)';

    elseif (servertype = DS_ORACLE_DBMS) then

	EXECUTE IMMEDIATE
	'create table manufacturer ' +
	'( ' +
	    'manufacturer_id	varchar2(8)	not null, ' +
	    'manufacturer_desc	varchar(15)	not null, ' +
	    'country_code	char(3)		not null, ' +
	    'logo_handle	varchar(76)	not null, ' +
	    'logo_select_handle	varchar(76)	not null, ' +
	    'CONSTRAINT manufacturer_pk '	+
	    'PRIMARY KEY (manufacturer_id) '	+
	')';

    elseif (servertype = DS_SQLSERVER_DBMS) then

	EXECUTE IMMEDIATE
	'create table manufacturer ' +
	'( ' +
	    'manufacturer_id	char(8)		not null, ' +
	    'manufacturer_desc	varchar(15)	not null, ' +
	    'country_code	char(3)		not null, ' +
	    'logo_handle	varchar(76)	not null, ' +
	    'logo_select_handle	varchar(76)	not null ' +
	')';

	EXECUTE IMMEDIATE
	'create unique clustered index manufacturer_mod ' +
	'on manufacturer (manufacturer_id)';

    endif;

    callproc do_grant_and_synonym(name = 'manufacturer');

/*
**  Turn autocommit off
*/
CurProcedure.DBSession.SetAutocommit(state = FALSE);


bo.FileHandle = BmFilePath + 'niss.gif';
status = bo.InsertIntoDB(tablename= 'openroad_images');
if status = ER_OK then
   logo_dbh = bo.DbHandle;
   bo.FileHandle = BmFilePath +  'niss2.gif';
   status = bo.InsertIntoDB(tablename= 'openroad_images');
   if status = ER_OK then
	logo2_dbh = bo.DbHandle;
insert into manufacturer 
values('NISS', 'NISSAN', 'NA',logo_dbh, logo2_dbh);
   endif;
endif;
commit;

bo.FileHandle = BmFilePath + 'chr.gif';
status = bo.InsertIntoDB(tablename= 'openroad_images');
if status = ER_OK then
   logo_dbh = bo.DbHandle;
   bo.FileHandle = BmFilePath +  'chr2.gif';
   status = bo.InsertIntoDB(tablename= 'openroad_images');
   if status = ER_OK then
	logo2_dbh = bo.DbHandle;
insert into manufacturer 
values('CHR', 'Chrysler', 'NA',logo_dbh, logo2_dbh);
   endif;
endif;
commit;

bo.FileHandle = BmFilePath + 'ford.gif';
status = bo.InsertIntoDB(tablename= 'openroad_images');
if status = ER_OK then
   logo_dbh = bo.DbHandle;
   bo.FileHandle = BmFilePath +  'ford2.gif';
   status = bo.InsertIntoDB(tablename= 'openroad_images');
   if status = ER_OK then
	logo2_dbh = bo.DbHandle;
insert into manufacturer 
values('FORD', 'Ford', 'NA',logo_dbh, logo2_dbh);
   endif;
endif;
commit;

bo.FileHandle = BmFilePath + 'gm.gif';
status = bo.InsertIntoDB(tablename= 'openroad_images');
if status = ER_OK then
   logo_dbh = bo.DbHandle;
   bo.FileHandle = BmFilePath +  'gm2.gif';
   status = bo.InsertIntoDB(tablename= 'openroad_images');
   if status = ER_OK then
	logo2_dbh = bo.DbHandle;
insert into manufacturer 
values('GM', 'General Motors', 'NA',logo_dbh, logo2_dbh);
   endif;
endif; 
commit;

/*
**  Turn autocommit on again.
*/
CurProcedure.DBSession.SetAutocommit(state = TRUE);

end;

			/*
**  History:
**
**	11-oct-95 (fraser)
**	    DBI change.  Added code to build tables for OpenIngres/Desktop.
**	    Removed commit statements which are no longer necessary because this
**	    procedure is now run in autocommit mode.
**	26-oct-95 (fraser)
**	    Added code for Oracle and SQL Server.
**	07-nov-96 (sandyd)
**	    Adapted DS_INGRES_DBMS case to work on gateways and NativeIngres.
*/

procedure buildmodel_class
( 
) =

declare
    tablename	= varchar(24) not null;
    ret_stat	= smallint not null;
    servertype	= integer not null;
enddeclare
begin
    tablename = 'model_class' ;
    ret_stat = Callproc CkTableExists(param_tbl = :tablename);
    if ret_stat = TableFound then 
	Drop table model_class; 
    endif;

    servertype = CurProcedure.DBSession.ServerType;

    if (servertype = DS_INGRES_DBMS) then

	create table model_class(
	    model_class_id	char(2)		not null with default,
	    model_class_desc	varchar(20)	not null with default
	)
	with noduplicates;

	if (NativeIngres = TRUE) then

	    modify model_class to btree unique on model_class_id
		with nonleaffill = 80, leaffill = 70, fillfactor = 80;

	else

	    create unique index model_class_mod on model_class (model_class_id);

	endif;

    elseif (servertype = DS_INGRESDSK_DBMS) then

	EXECUTE IMMEDIATE
	'create table model_class ' +
	'( ' +
	    'model_class_id	char(2)		not null with default, ' +
	    'model_class_desc	varchar(20)	not null with default, ' +

	    'PRIMARY KEY (model_class_id)' +
	')';

	EXECUTE IMMEDIATE
	'create unique index model_class_mod ' +
	'on model_class (model_class_id)';

    elseif (servertype = DS_ORACLE_DBMS) then

	EXECUTE IMMEDIATE
	'create table model_class ' +
	'( ' +
	    'model_class_id	char(2)		not null, ' +
	    'model_class_desc	varchar(20)	not null, ' +
	    'CONSTRAINT model_class_pk '	+
	    'PRIMARY KEY (model_class_id)'	+
	')';

    elseif (servertype = DS_SQLSERVER_DBMS) then

	EXECUTE IMMEDIATE
	'create table model_class ' +
	'( ' +
	    'model_class_id	char(2)		not null, ' +
	    'model_class_desc	varchar(20)	not null ' +
	')';

	EXECUTE IMMEDIATE
	'create unique clustered index model_class_mod ' +
	'on model_class (model_class_id)';

    endif;

    callproc do_grant_and_synonym(name = 'model_class');


insert into model_class(model_class_id,model_class_desc)values('AA',
'4 Door Sedan');

insert into model_class(model_class_id,model_class_desc)values('BB',
'2 Door Coupe');

insert into model_class(model_class_id,model_class_desc)values('CC',
'Sports Car');

insert into model_class(model_class_id,model_class_desc)values('DD','Convertible');

insert into model_class(model_class_id,model_class_desc)values('EE',
'Sport Utility');

insert into model_class(model_class_id,model_class_desc)values('FF',
'5 Door MiniVan');

end

