	    'color_id		char(8)		not null ' +
	')';

	EXECUTE IMMEDIATE
	'create unique clustered index model_ext_colo_mod ' +
	'on model_ext_color (model_id, color_id)';

    endif;

    callproc do_grant_and_synonym(name = 'model_ext_color');

