			/*
**  History:
**
**	11-oct-95 (fraser)
**	    DBI change.  Added code to build tables for OpenIngres/Desktop.
**	    Removed commit statements which are no longer necessary because this
**	    procedure is now run in autocommit mode.
**	26-oct-95 (fraser)
**	    Added code for Oracle and SQL Server.
**	15-feb-96 (fraser)
**	    Oracle:  Changed model_id to varchar2.
**	07-nov-96 (sandyd)
**	    Adapted DS_INGRES_DBMS case to work on gateways and NativeIngres.
*/

 procedure buildmodel_int_color
( 
) =
declare
    tablename	= varchar(24) not null;
    ret_stat	= smallint not null;
    servertype	= integer not null;
enddeclare
begin
    tablename = 'model_int_color' ;
    ret_stat = Callproc CkTableExists(param_tbl = :tablename);
    if ret_stat = TableFound then 
	Drop table model_int_color; 
    endif;

    servertype = CurProcedure.DBSession.ServerType;

    if (servertype = DS_INGRES_DBMS) then

	create table model_int_color(
	    model_id		char(8)		not null,
	    color_desc		varchar(30)	not null,
	    color_id		char(8)		not null
	)
	with noduplicates;

	if (NativeIngres = TRUE) then

	    modify model_int_color to btree unique on model_id, color_id
		with nonleaffill = 80, leaffill = 70, fillfactor = 80;

	else

	    create unique index model_int_colo_mod on model_int_color
		(model_id, color_id);

	endif;

    elseif (servertype = DS_INGRESDSK_DBMS) then

	EXECUTE IMMEDIATE
	'create table model_int_color ' +
	'( ' +
	    'model_id		char(8)		not null, ' +
	    'color_desc		varchar(30)	not null, ' +
	    'color_id		char(8)		not null, ' +

	    'PRIMARY KEY (model_id, color_id)' +
	')';

	EXECUTE IMMEDIATE
	'create unique index model_int_colo_mod ' +
	'on model_int_color (model_id, color_id)';

    elseif (servertype = DS_ORACLE_DBMS) then

	EXECUTE IMMEDIATE
	'create table model_int_color ' +
	'( ' +
	    'model_id		varchar2(8)	not null, ' +
	    'color_desc		varchar(30)	not null, ' +
	    'color_id		char(8)		not null, ' +
	    'CONSTRAINT model_int_color_pk '	+
	    'PRIMARY KEY (model_id, color_id)'	+
	')';

    elseif (servertype = DS_SQLSERVER_DBMS) then

	EXECUTE IMMEDIATE
	'create table model_int_color ' +
	'( ' +
	    'model_id		char(8)		not null, ' +
	    'color_desc		varchar(30)	not null, ' +
	    'color_id		char(8)		not null ' +
	')';

	EXECUTE IMMEDIATE
	'create unique clustered index model_int_colo_mod ' +
	'on model_int_color (model_id, color_id)';
    endif;

    callproc do_grant_and_synonym(name = 'model_int_color');

insert into model_int_color
values ('CHRYLEB', 'mottled beige', 'BEIGE');
