procedure buildoptions
( 
) =
declare
    tablename	= varchar(24) not null;
    ret_stat	= smallint not null;
    servertype	= integer not null;
enddeclare
begin
    tablename = 'options' ;
    ret_stat = Callproc CkTableExists(param_tbl = :tablename);
    if ret_stat = TableFound then 
	Drop table options; 
    endif;

    servertype = CurProcedure.DBSession.ServerType;

    if (servertype = DS_INGRES_DBMS) then

	create table options(
	    option_id		char(8)		not null with default,
	    option_desc		varchar(80)	not null with default
	)
	with duplicates;

	if (NativeIngres = TRUE) then

	    modify options to btree unique on option_id
		with nonleaffill = 80, leaffill = 70, fillfactor = 80;

	else

	    create unique index options_mod on options (option_id);

	endif;

    elseif (servertype = DS_INGRESDSK_DBMS) then

	EXECUTE IMMEDIATE
	'create table options ' +
	'( ' +
	    'option_id		char(8)		not null with default, ' +
	    'option_desc	varchar(80)	not null with default, ' +

	    'PRIMARY KEY (option_id)' +
	')';

	EXECUTE IMMEDIATE
	'create unique index options_mod ' +
	'on options (option_id)';

    elseif (servertype = DS_ORACLE_DBMS) then

	EXECUTE IMMEDIATE
	'create table options ' +
	'( ' +
	    'option_id		char(8)		not null, ' +
	    'option_desc	varchar(80)	not null, ' +
	    'CONSTRAINT options_mod_pk '	+
	    'PRIMARY KEY (option_id)'		+
	')';

    elseif (servertype = DS_SQLSERVER_DBMS) then

	EXECUTE IMMEDIATE
	'create table options ' +
	'( ' +
	    'option_id		char(8)		not null, ' +
	    'option_desc	varchar(80)	not null ' +
	')';

	EXECUTE IMMEDIATE
	'create unique clustered index options_mod ' +
	'on options (option_id)';

    endif;

    callproc do_grant_and_synonym(name = 'options');


