			/*
**  History:
**	12-oct-95 (clays)
**		Added code to create tables for Ingres/Desktop and
**		removed unnecessary commits since run in autocommit mode.
**	18-oct-95 (clays)
**		Added code to create tables for Oracle DBMS.
**	23-oct-95 (clays)
**		Added code to create tables for SQL Server DBMS.
**	07-nov-96 (sandyd)
**	    Split DS_INGRES_DBMS case into NativeIngres versus gateway SQL.
**
*/

procedure buildorder_items
( 
) =
declare
	tablename = varchar(24) not null;
	ret_stat = smallint not null;
enddeclare
begin
	tablename = 'order_items';
	ret_stat = Callproc CkTableExists(param_tbl = :tablename);
	if ret_stat = TableFound then 
		Drop table order_items; 
	endif;

	if ( CurProcedure.DBSession.ServerType = DS_INGRES_DBMS ) then

	    if (NativeIngres = TRUE) then

		create table order_items
		    ( 
		      order_no 	  	integer not null,
		      part_no 	  	integer not null,
		      quantity 	  	integer,
		      sale_price   	money,
		      orderitem_status 	char(1) not null
		    )	
		    with noduplicates;

		modify order_items to btree unique on order_no, part_no
		    with nonleaffill = 80, leaffill    = 70, fillfactor  = 80;

	    else

		create table order_items
		    ( 
		      order_no 	  	integer not null,
		      part_no 	  	integer not null,
		      quantity 	  	integer,
		      sale_price   	float /*money*/,
		      orderitem_status 	char(1) not null
		    );	

		create unique index order_items_mod on order_items
		    (order_no, part_no);

	    endif;

	elseif ( CurProcedure.DBSession.ServerType = DS_INGRESDSK_DBMS ) then

	    EXECUTE IMMEDIATE
	    'create table order_items ' +
		'( ' +
		'  order_no 	    integer 	not null, ' +
		'  part_no 	    integer 	not null, ' +
		'  quantity 	    integer, ' +
		'  sale_price  	    decimal(14,2), ' +
		'  orderitem_status char(1) 	not null, ' +

		'  PRIMARY KEY( order_no, part_no ) ' +
		')';

	    EXECUTE IMMEDIATE
	    'create unique index order_items_mod ' +
	        'on order_items( order_no, part_no )';

	elseif ( CurProcedure.DBSession.ServerType = DS_ORACLE_DBMS ) then

	    EXECUTE IMMEDIATE
	    'create table order_items ' +
		'( ' +
		'  order_no 	    integer 	constraint nn_order_no not null, ' +
		'  part_no 	    integer 	constraint nn_part_no  not null, ' +
		'  quantity 	    integer, ' +
		'  sale_price  	    decimal(14,2), ' +
		'  orderitem_status char(1) 	constraint nn_orderitem_status not null, ' +

		'  constraint pk_order_items ' +
		'      UNIQUE( order_no, part_no ) ' +
		')';

	elseif ( CurProcedure.DBSession.ServerType = DS_SQLSERVER_DBMS ) then

	    EXECUTE IMMEDIATE
	    'create table order_items ' +
		'( ' +
		'  order_no 	    int 	not null, ' +
		'  part_no 	    int 	not null, ' +
		'  quantity 	    int		null, ' +
		'  sale_price  	    money	null, ' +
		'  orderitem_status char(1) 	not null ' +
		')';

	    EXECUTE IMMEDIATE
	    'create unique index order_items_mod ' +
	        'on order_items( order_no, part_no ) ' +
                'with fillfactor = 80 ';

	endif;	

	CALLPROC do_grant_and_synonym( name = 'order_items' );

insert into order_items (order_no, part_no, quantity, sale_price, 
orderitem_status)
