			/*
**  History:
**	12-oct-95 (clays)
**		Added code to create tables for Ingres/Desktop and
**		removed unnecessary commits since run in autocommit mode.
**	18-oct-95 (clays)
**		Added code to create tables for Oracle DBMS.
**	23-oct-95 (clays)
**		Added code to create tables for SQL Server DBMS.
**	07-nov-96 (sandyd)
**	    Split DS_INGRES_DBMS case into NativeIngres versus gateway SQL.
**	    Also had to decorate date inserts with "date()" to satisfy DB2.
**
*/

procedure buildorders
( 
) =
declare
	tablename = varchar(24) not null;
	ret_stat = smallint not null;
enddeclare
begin
	tablename = 'orders';
	ret_stat = Callproc CkTableExists(param_tbl = :tablename);
	if ret_stat = TableFound then
		Drop table orders; 
	endif;

	if ( CurProcedure.DBSession.ServerType = DS_INGRES_DBMS ) then

	    if (NativeIngres = TRUE) then

		create table orders
		    ( 
		      order_no 		integer 	not null,
		      order_date 	date,
		      cust_no 		integer,
		      total 		money,
		      status 		smallint 	not null
		    )
		    with noduplicates;

		modify orders to btree unique on order_no
		    with nonleaffill = 80, leaffill    = 70, fillfactor  = 80;	

	    else

		create table orders
		    ( 
		      order_no 		integer 	not null,
		      order_date 	date,
		      cust_no 		integer,
		      total 		float /*money*/,
		      status 		smallint 	not null
		    );

		create unique index orders_mod on orders (order_no);

	    endif;

	elseif ( CurProcedure.DBSession.ServerType = DS_INGRESDSK_DBMS ) then

	    EXECUTE IMMEDIATE
	    'create table orders ' +
		'( ' + 
		 ' order_no 	integer 	not null, ' +
		 ' order_date 	datetime, ' +
		 ' cust_no 	integer, ' +
		 ' total 	decimal(14,2), ' +
		 ' status 	smallint 	not null, ' +

		 ' PRIMARY KEY( order_no )' +
		')';

	    EXECUTE IMMEDIATE
	    'create unique index orders_mod ' +
	        'on orders( order_no )';

	elseif ( CurProcedure.DBSession.ServerType = DS_ORACLE_DBMS ) then

	    EXECUTE IMMEDIATE
	    'create table orders ' +
		'( ' + 
		 ' order_no 	integer 	constraint nn_order_no1 not null, ' +
		 ' order_date 	date, ' +
		 ' cust_no 	integer, ' +
		 ' total 	decimal(14,2), ' +
		 ' status 	smallint 	constraint nn_status1   not null, ' +

		 ' constraint pk_orders ' +
		 '    UNIQUE( order_no ) ' +
		')';

	EXECUTE IMMEDIATE
	'alter session set NLS_DATE_FORMAT=''DD-MON-YYYY'' ';

	elseif ( CurProcedure.DBSession.ServerType = DS_SQLSERVER_DBMS ) then

	    EXECUTE IMMEDIATE
	    'create table orders ' +
		'( ' + 
		 ' order_no 	int	 	not null, ' +
		 ' order_date 	datetime	null, ' +
		 ' cust_no 	int		null, ' +
		 ' total 	money		null, ' +
		 ' status 	smallint 	not null ' +
		')';

	    EXECUTE IMMEDIATE
	    'create unique index orders_mod ' +
	        'on orders( order_no ) ' +
		'with fillfactor = 80 ';

	endif;

	CALLPROC do_grant_and_synonym( name = 'orders' );

insert into orders(order_no, order_date, cust_no, total, status)
