**  History:
**
**	11-oct-95 (fraser)
**	    DBI change.  Added code to build tables for OpenIngres/Desktop.
**	    Removed commit statements whcih are no longer needed because this
**	    procedure is now run in autocommit mode.
**	26-oct-95 (fraser)
**	    Added code for Oracle and SQL Server.
**	07-nov-96 (sandyd)
**	    Adapted DS_INGRES_DBMS case to work on gateways and NativeIngres.
*/

procedure buildpackage_items
( 
) =
declare
    tablename	= varchar(24) not null;
    ret_stat	= smallint not null;
    servertype	= integer not null;
enddeclare
begin
    tablename = 'package_items' ;
    ret_stat = Callproc CkTableExists(param_tbl = :tablename);
    if ret_stat = TableFound then 
	Drop table package_items;
    endif;

    servertype = CurProcedure.DBSession.ServerType;

    if (servertype = DS_INGRES_DBMS) then

	create table package_items(
	    package_id		char(8)		not null with default,
	    item_nbr		smallint	not null with default,
	    option_id		char(8)		not null with default
	)
	with duplicates;

	if (NativeIngres = TRUE) then

	    modify package_items to btree unique on package_id, option_id
		with nonleaffill = 80, leaffill = 70, fillfactor = 80;

	else

	    create unique index package_items_mod on package_items
		(package_id, option_id);

	endif;

    elseif (servertype = DS_INGRESDSK_DBMS) then

	EXECUTE IMMEDIATE
	'create table package_items ' +
	'( ' +
	    'package_id		char(8)		not null with default, ' +
	    'item_nbr		smallint	not null with default, ' +
	    'option_id		char(8)		not null with default, ' +

	    'PRIMARY KEY (package_id, option_id)' +
	')';

	EXECUTE IMMEDIATE
	'create unique index package_items_mod ' +
	'on package_items (package_id, option_id)';

    elseif (servertype = DS_ORACLE_DBMS) then

	EXECUTE IMMEDIATE
	'create table package_items ' +
	'( ' +
	    'package_id		char(8)		not null, ' +
	    'item_nbr		smallint	not null, ' +
	    'option_id		char(8)		not null, ' +
	    'CONSTRAINT package_items_pk '	  +
	    'PRIMARY KEY (package_id, option_id)' +
	')';

    elseif (servertype = DS_SQLSERVER_DBMS) then

	EXECUTE IMMEDIATE
	'create table package_items ' +
	'( ' +
	    'package_id		char(8)		not null, ' +
	    'item_nbr		smallint	not null, ' +
	    'option_id		char(8)		not null ' +
	')';

	EXECUTE IMMEDIATE
	'create unique clustered index package_items_mod ' +
	'on package_items (package_id, option_id)';

    endif;

    callproc do_grant_and_synonym(name = 'package_items');

