procedure buildv_checkout
( 
) =
declare
    tablename = varchar(24) not null;
    ret_stat = smallint not null;
enddeclare
begin
    tablename = 'v_checkout' ;
    ret_stat = Callproc CkTableExists(param_tbl = :tablename);
    if ret_stat = TableFound then 
	Drop table v_checkout; 
    endif;

    if (CurProcedure.DBSession.ServerType = DS_INGRES_DBMS) then

	if (NativeIngres = TRUE) then

	    create table v_checkout(
		acctno 		integer 	with null,
		vid_no 		integer 	with null,
		tape_no 	integer 	with null,
		date_out 	date 		with null,
		date_in 	date 		with null,
		price 		money 		with null
	    )
	    with noduplicates;

	    modify v_checkout to cheap;

	else

	    create table v_checkout(
		acctno 		integer 	with null,
		vid_no 		integer 	with null,
		tape_no 	integer 	with null,
		date_out 	date 		with null,
		date_in 	date 		with null,
		price 		float /*money*/	with null
	    );

	endif;

    elseif (CurProcedure.DBSession.ServerType = DS_INGRESDSK_DBMS) then

	EXECUTE IMMEDIATE
	'create table v_checkout(	' +
    	'    acctno 	integer, 	' +
	'    vid_no 	integer, 	' +
	'    tape_no 	integer, 	' +
	'    date_out 	datetime, 	' +
	'    date_in 	datetime, 	' +
	'    price	decimal(14,2) 	' +
	')';

    elseif (CurProcedure.DBSession.ServerType = DS_SQLSERVER_DBMS) then

	EXECUTE IMMEDIATE
	'create table v_checkout(		' +
    	'    acctno 	int		null,	' +
	'    vid_no 	int		null,	' +
	'    tape_no 	int		null,	' +
	'    date_out 	datetime 	null, 	' +
	'    date_in 	datetime 	null, 	' +
	'    price	money	 	null	' +
	')';

    elseif (CurProcedure.DBSession.ServerType = DS_ORACLE_DBMS) then

	EXECUTE IMMEDIATE
	'create table v_checkout(	' +
    	'    acctno 	number(10), 	' +
	'    vid_no 	number(10), 	' +
	'    tape_no 	number(10), 	' +
	'    date_out 	date, 		' +
	'    date_in 	date,	 	' +
	'    price	number(14,2) 	' +
	')';


	EXECUTE IMMEDIATE
	'alter session set NLS_DATE_FORMAT = ''DD-MON-YYYY''';

    endif;

    callproc do_grant_and_synonym(name = 'v_checkout');

insert into v_checkout(acctno,vid_no,tape_no,date_out,date_in,price)
