elseif (CurProcedure.DBSession.ServerType = DS_ORACLE_DBMS) then
EXECUTE IMMEDIATE 'create table v_highest_number( ' + ' h_number number(10) not null, ' + ' tbl_name varchar2(32) not null ' + ')';
endif;
callproc do_grant_and_synonym(name = 'v_highest_number');
