insert into v_permemp values (115, date('30-jun-1994'), NULL, 0, NULL, 0.000);
commit; end

/* ** History: ** ** 19-oct-95 (timt) ** DBI change. Added code to create tables for OpenIngres/Desktop. ** Removed commit statements which are unnecessary because this ** procedure is now run in autocommit mode. ** ** 23-oct-95 (timt) ** DBI change. Added code to create tables for Oracle and SQL Server. ** Removed commit statements which are unnecessary because this ** procedure is now run in autocommit mode. ** ** 07-nov-96 (sandyd) ** Adapted DS_INGRES_DBMS case to work on gateways and NativeIngres. */
procedure buildv_store ( ) = declare tablename = varchar(24) not null; ret_stat = smallint not null; enddeclare begin tablename = 'v_store' ; ret_stat = Callproc CkTableExists(param_tbl = :tablename); if ret_stat = TableFound then Drop table v_store; endif;
if (CurProcedure.DBSession.ServerType = DS_INGRES_DBMS) then
create table v_store( sname varchar(50) with null, saddr varchar(50) with null, scity varchar(20) with null, sstate varchar(2) with null, szip varchar(5) with null, slogo integer with null ) with noduplicates;
if (NativeIngres = TRUE) then
modify v_store to cheap;
endif;
elseif (CurProcedure.DBSession.ServerType = DS_INGRESDSK_DBMS) then
EXECUTE IMMEDIATE 'create table v_store( ' + ' sname varchar(50), ' + ' saddr varchar(50), ' + ' scity varchar(20), ' + ' sstate varchar(2), ' + ' szip varchar(5), ' + ' slogo integer ' + ')';
elseif (CurProcedure.DBSession.ServerType = DS_SQLSERVER_DBMS) then
EXECUTE IMMEDIATE 'create table v_store( ' + ' sname varchar(50) null, ' + ' saddr varchar(50) null, ' + ' scity varchar(20) null, ' + ' sstate varchar(2) null, ' + ' szip varchar(5) null, ' + ' slogo int null ' + ')';
elseif (CurProcedure.DBSession.ServerType = DS_ORACLE_DBMS) then
EXECUTE IMMEDIATE 'create table v_store( ' + ' sname varchar2(50), ' + ' saddr varchar2(50), ' + ' scity varchar2(20), ' + ' sstate varchar2(2), ' + ' szip varchar2(5), ' + ' slogo number(10) ' + ')';
endif;
callproc do_grant_and_synonym(name = 'v_store');
insert into v_store(sname, saddr, scity, sstate, szip, slogo) values ('Classy Video Company', '1414 Fillmore Street', 'San Francisco', 'CA', '95220', NULL);
end

