COMPONENT.script:
			/*
**  Frame script for 
**
**  Input:
**
**	app_name - the name of the application
**
**  Output:
**
**	reload_app - if TRUE, load application and tables
**
**  Returns:
**
**	PU_OK - go ahead
**	PU_CANCEL - cancel
**	
**
**  History:
**
**	27-oct-95 (fraser)
**	    Created.
*/

initialize(
    app_name	= varchar(50) not null;
    reload_app	= integer not null;
) =

declare

enddeclare
{
    ef_text = 'There is already a copy of the ' + app_name + ' application ' +
	' in the database.' + HC_NEWLINE + HC_NEWLINE +

	'Do you want to load a fresh copy of the ' + app_name + ' application' +
	' as well as create the tables for the application?';
}


on click ok_btn =
{
    if (field(rf_select).CurEnumValue = 1) then
	reload_app = FALSE;
    else
	reload_app = TRUE;
    endif;

    return(PU_OK);
}

on click cancel_btn =
{
    return(PU_CANCEL);
}

